import React, { useState } from 'react';

export default function ProfileImage(props) {
    console.log(props);

    const [image, setImage] = useState(props);

    // this.state.width = this.state.height = props.size || 200;
    this.state.width = this.state.height = props.size || 200;
    this.state.url = props.url || 'ttps://placeimg.com/300/300/animals';
    // this.state.url = props.url || 'ttps://placeimg.com/300/300/animals';


    const style = {
        display: 'inline-block',
        width: 100,
        height: 100,
        backgroundImage: 'url(https://placeimg.com/300/300/any)',
        backgroundSize: 'cover',
        borderRadius: '50%',
    };

    return (
        <>
            <span style={style} />
        </>
    );

}
